// LENS Configuration - Extract and validate input
const input = $input.first().json;
const body = input.body || input;

// Required fields
const url = body.url || '';
const platform = body.platform || 'unknown';
const scanId = body.scanId || '';
const findingId = body.findingId || '';
const userId = body.userId || '';

// Validation
if (!url || !url.startsWith('http')) {
  return {
    json: {
      error: true,
      message: 'Invalid or missing URL',
      url,
      platform,
      scanId
    }
  };
}

// Extract domain for analysis
let domain = '';
try {
  const urlObj = new URL(url);
  domain = urlObj.hostname.replace('www.', '');
} catch (e) {
  domain = url;
}

return {
  json: {
    url,
    platform,
    scanId,
    findingId,
    userId,
    domain,
    error: false
  }
};

// SSL/TLS Analysis
const config = $('âš™ï¸ LENS Config').first().json;
const url = config.url;

let sslInfo = {
  hasSSL: false,
  isValid: false,
  status: 'None',
  score: 0
};

try {
  const urlObj = new URL(url);
  sslInfo.hasSSL = urlObj.protocol === 'https:';

  if (sslInfo.hasSSL) {
    // Basic SSL check - in production, use an SSL API
    sslInfo.isValid = true;
    sslInfo.status = 'Valid Certificate';
    sslInfo.score = 100;
  } else {
    sslInfo.status = 'No SSL/TLS';
    sslInfo.score = 0;
  }
} catch (e) {
  sslInfo.status = 'Error checking SSL';
  sslInfo.score = 0;
}

return { json: sslInfo };

// Platform Consistency Analysis
const config = $('âš™ï¸ LENS Config').first().json;
const url = config.url;
const platform = config.platform.toLowerCase();
const domain = config.domain;

// Known platform domains
const platformDomains = {
  'twitter': ['twitter.com', 'x.com'],
  'x': ['twitter.com', 'x.com'],
  'facebook': ['facebook.com', 'fb.com'],
  'instagram': ['instagram.com'],
  'linkedin': ['linkedin.com'],
  'github': ['github.com'],
  'reddit': ['reddit.com'],
  'youtube': ['youtube.com', 'youtu.be'],
  'tiktok': ['tiktok.com'],
  'pinterest': ['pinterest.com'],
  'tumblr': ['tumblr.com'],
  'medium': ['medium.com'],
  'discord': ['discord.com', 'discord.gg'],
  'twitch': ['twitch.tv'],
  'snapchat': ['snapchat.com'],
  'telegram': ['t.me', 'telegram.org'],
  'whatsapp': ['whatsapp.com'],
  'spotify': ['spotify.com'],
  'soundcloud': ['soundcloud.com'],
  'behance': ['behance.net'],
  'dribbble': ['dribbble.com'],
  'flickr': ['flickr.com'],
  'vimeo': ['vimeo.com'],
  'patreon': ['patreon.com'],
  'etsy': ['etsy.com'],
  'ebay': ['ebay.com'],
  'amazon': ['amazon.com', 'amazon.co.uk']
};

let result = {
  platform: platform,
  expectedDomains: [],
  actualDomain: domain,
  isConsistent: false,
  consistency: 'Unknown',
  score: 50 // Default neutral score
};

// Check if platform is known
if (platformDomains[platform]) {
  result.expectedDomains = platformDomains[platform];
  result.isConsistent = platformDomains[platform].some(d => domain.includes(d));

  if (result.isConsistent) {
    result.consistency = 'High';
    result.score = 100;
  } else {
    result.consistency = 'Low';
    result.score = 20;
  }
} else {
  // Unknown platform - neutral scoring
  result.consistency = 'Unknown';
  result.score = 60;
}

return { json: result };

// LENS Score Calculator - Compute confidence score
const config = $('âš™ï¸ LENS Config').first().json;
const urlFetch = $('ğŸŒ Fetch URL Content').first().json;
const sslCheck = $('ğŸ”’ SSL Check').first().json;
const platformCheck = $('ğŸ“Š Platform Check').first().json;

// Initialize scores
let scores = {
  contentScore: 0,
  sslScore: sslCheck.score || 0,
  platformScore: platformCheck.score || 50
};

// 1. Content Score - Based on HTTP response
const statusCode = urlFetch.statusCode || 0;
if (statusCode === 200) {
  scores.contentScore = 100;
} else if (statusCode >= 300 && statusCode < 400) {
  scores.contentScore = 70; // Redirect
} else if (statusCode === 404) {
  scores.contentScore = 15; // Page not found
} else if (statusCode === 403) {
  scores.contentScore = 40; // Forbidden (might still exist)
} else if (statusCode >= 500) {
  scores.contentScore = 30; // Server error
} else {
  scores.contentScore = 20; // Unknown/error
}

// Calculate weighted confidence score
const weights = {
  content: 0.40,
  ssl: 0.25,
  platform: 0.35
};

const confidenceScore = Math.round(
  (scores.contentScore * weights.content) +
  (scores.sslScore * weights.ssl) +
  (scores.platformScore * weights.platform)
);

// Build metadata
const metadata = {
  sourceAge: 'Unknown', // Would need WHOIS API for this
  sslStatus: sslCheck.status,
  platformConsistency: platformCheck.consistency,
  httpStatus: statusCode,
  scoreBreakdown: scores
};

// Determine verification status
let verificationStatus = 'unverified';
if (confidenceScore >= 80) {
  verificationStatus = 'verified';
} else if (confidenceScore >= 50) {
  verificationStatus = 'partial';
} else {
  verificationStatus = 'low_confidence';
}

return {
  json: {
    url: config.url,
    platform: config.platform,
    scanId: config.scanId,
    findingId: config.findingId,
    userId: config.userId,
    confidenceScore,
    metadata,
    verificationStatus,
    analysisTimestamp: new Date().toISOString()
  }
};

// LENS Hash Generator - Create cryptographic proof
const crypto = require('crypto');
const input = $input.first().json;

// Create evidence snapshot for hashing
const evidenceContent = JSON.stringify({
  url: input.url,
  platform: input.platform,
  scanId: input.scanId,
  findingId: input.findingId,
  confidenceScore: input.confidenceScore,
  metadata: input.metadata,
  verificationStatus: input.verificationStatus,
  timestamp: input.analysisTimestamp
}, null, 2);

// Generate SHA-256 hash
const verificationHash = crypto
  .createHash('sha256')
  .update(evidenceContent)
  .digest('hex');

// Create content preview (for display)
const hashedContent = evidenceContent.length > 500 
  ? evidenceContent.substring(0, 500) + '...'
  : evidenceContent;

return {
  json: {
    confidenceScore: input.confidenceScore,
    hashedContent: hashedContent,
    verificationHash: verificationHash,
    metadata: input.metadata,
    verificationStatus: input.verificationStatus,
    // Pass through for Supabase callback
    scanId: input.scanId,
    findingId: input.findingId,
    userId: input.userId,
    url: input.url,
    platform: input.platform
  }
};
